import {useState} from 'react'
import "./Evento.css"
export function Circulo(props){
const [encendido, setEncendido] = useState(false);
return(
<div>
    <div>
        <div className={'circulos ${encendido ? "encendido" : "apagado"} '}></div>

    <button onClick={(e)=> setEncendido(!encendido)} >Encendido/ Apagado</button>

    </div>
</div>
);
}
