import {useState} from 'react'
export function Evento(props) {
  const [pregunta, setPregunta] = useState("");
  return (
    <div>
      <div>
        <button
          onClick={() => {
            alert("Hola");
          }}
        >
          Hola
        </button>
      </div>

      <div>
        <button
          onClick={(e) => {
            console.log("Adios");
          }}
        >
          Adios
        </button>
      </div>
      <div>
        <button 
       onClick={(e) => 
       pregunta!=="Pregunta"
       ? setPregunta("Esto lo tengo dominado") : setPregunta("Pregunta")
       }>
           {pregunta}
        </button>
      </div>
    </div>
  );
}
